import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import UserServices from "../services/usernameServices";
import Swal from "sweetalert2";
import AuthServices from '../services/authServices';

const PasswordUpdate = () => {
    
    const { idUser } = useParams();

    const initialPasswordState = {
        idUser: null,
        currentPassword: "",
        newPassword: ""
    }

    const [password, setPassword] = useState(initialPasswordState);
    const [validPassword, setValidPassword] = useState({});

    const handleInputChange = event => {
        const { name, value } = event.target;
        setPassword({ ...password, [name]: value });
    };

    const handleInputblurPassword = (event) => {
        handleInputChange(event);
        setValidPassword(validationPassword(password));
    };

    const validationPassword = (password) => {
        let validPassword = {}

        if (
            password.newPassword.length < 8 ||
            !/[A-Z]/.test(password.newPassword) ||
            !/[0-9]/.test(password.newPassword) ||
            !/[!@#$%^&*]/.test(password.newPassword)
        ) {
            validPassword.password =
                "La contraseña debe tener al menos 8 caracteres, una letra mayúscula, un número y un carácter especial";
        }

        return validPassword;
    }

    const passwordUpdate = (e) => {
        e.preventDefault()
        const token = AuthServices.getAuthToken();
        if (token) {
            UserServices.setAuthToken(token);
        } else {
            console.error("No se encontró un token válido");
            return;
        }
        setValidPassword(validationPassword(User));

        if (Object.keys(validPassword).length === 0) {
            UserServices.updatePassword(idUser, password)
                .then(response => {
                    console.log(response.data);
                    Swal.fire({
                        position:'top-center',
                        icon: 'success',
                        title: 'contraseña Actualizada Correctamente',
                        showConfirmButton: false,
                        timer: 2200
                    })

                })
                .catch(e => {
                    console.log(e);
                });
        }

    };


    return (
        <div className="container ">
        <div className="card  ">
            <div className="card-body ">
                <blockquote className="blockquote mb-0 ">

                    <form novalidate onSubmit={passwordUpdate}
                        className="row g-3 needs-validation my-3  border = 1" >

                        <div className="col-md-3 position-relative">

                            <label for="description" className="form-label ">Descripción</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text">
                                    <i className="bi bi-pencil-square"></i>
                                </span>
                                <input type="text" className="form-control" id="description" value={TypeCar.description} onChange={handleInputChange} name="description" required />
                               
                            </div>
                        </div>


                        <div className="col-12">
                            <button className="btn btn-secondary my-3 mx-2" type="submit">
                                <i className="bi bi-gear"> Actualizar</i>
                            </button>
                            <Link className="btn btn-danger" to={"/TypeCarList"}>
                                <i className="bi bi-x-circle"> Cancelar</i>
                            </Link>
                        </div>

                    </form>



                </blockquote>
            </div>
        </div>
        </div>

    );
};

export default PasswordUpdate;
