import React, { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import UserServices from "../services/usernameServices";
import Swal from "sweetalert2";
import AuthServices from '../services/authServices';

const PasswordUpdate = () => {
    const navigate = useNavigate();
    const { idUser } = useParams();

    const initialPasswordState = {
        id: idUser,
        currentPassword: "",
        newPassword: ""
    }

    const [password, setPassword] = useState(initialPasswordState);
    const [validPassword, setValidPassword] = useState({});

    const handleInputChange = event => {
        const { name, value } = event.target;
        setPassword({ ...password, [name]: value });
    };

    const handleInputblurPassword = (event) => {
        handleInputChange(event);
        setValidPassword(validationPassword(password));
    };


    const validationPassword = (password) => {
        let validPassword = {}

        if (
            password.newPassword.length < 8 ||
            !/[A-Z]/.test(password.newPassword) ||
            !/[0-9]/.test(password.newPassword) ||
            !/[!@#$%^&*]/.test(password.newPassword)
        ) {
            validPassword.password =
                "La contraseña debe tener al menos 8 caracteres, una letra mayúscula, un número y un carácter especial";
        }

        return validPassword;
    }

    const passwordUpdate = (e) => {
        e.preventDefault()
        const token = AuthServices.getAuthToken();
        if (token) {
            UserServices.setAuthToken(token);
          
        } else {
            console.error("No se encontró un token válido");
            return;
        }
        setValidPassword(validationPassword(password));
        if (Object.keys(validPassword).length === 0) {
            UserServices.updatePassword(password)
                .then(response => {
                    console.log(response.data);
                    Swal.fire({
                        position: 'top-center',
                        icon: 'success',
                        title: 'contraseña Actualizada Correctamente',
                        showConfirmButton: false,
                        timer: 2000
                    }).then(
                    navigate("/")
                    )
                })
                .catch(e => {
                    console.log(e);
                    Swal.fire('Digite la actual contraseña correcta')
                });
        }

    };


    return (

        <div className="login-container">
        <div className="content">
          <h2 style={{ color: 'white' }}>Inicio de Sesión</h2>
          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <div className="input-group">
                <span className="input-group-text">
                  <i className="bi bi-person"></i>
                </span>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  aria-describedby="emailHelp"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Username"
                  style={{ width: '300px' }}
                />
              </div>
            </div>
            <div className="mb-3">
              <div className="input-group">
                <span className="input-group-text">
                  <i className="bi bi-lock"></i>
                </span>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Password"
                />
              </div>
            </div>
            {errors && (
              <div className='errorDiv'>
                <small className="errorSmall" id="helpId" >
                  <i className="bi bi-exclamation-circle"> Usuario o contraseña incorrecta</i>
  
                </small>
              </div>
            )}
            <button type="submit" className="btn btn-secondary">
              Iniciar sesión
            </button>
          </form>
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Link className="btn btn-primary" style={{ marginTop: '5%' }} to={'/UserCreate'}>
              <i className="bi bi-person-plus"> Registrarse</i>
            </Link>
          </div>
        </div>
      </div>

       

    );
};

export default PasswordUpdate;
