import React, { useState } from "react";
import UserList from "./UserList";
const ModalListUser = (props) => {
    const { title, message, onClose, onConfirm } = props;
    const [selectedUserId, setSelectedUserId] = useState(null);
    const handleUserSelection = (userId) => {
        // Llamar a la función onSelectUser con el userId
        props.onSelectUser(userId);
    };

    const handleSelectUser = (userId) => {
        setSelectedUserId(userId);
    };


    return (
        <div className="modal" tabIndex="-1" role="dialog" style={{ display: "block" }}>
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">

                        <button
                            type="button"
                            className="close"
                            data-dismiss="modal"
                            aria-label="Close"
                            onClick={onClose}
                        >
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">

                        <UserList hideButtons={true} selectedUserId={selectedUserId} />
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal" onClick={onClose}>
                            Cancelar
                        </button>
                        <button type="button" className="btn btn-primary" onClick={(
                            console.log(selectedUserId),
                            handleUserSelection(userId)

                        )}>
                            Confirmar
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ModalListUser;
