import React, { useState, useEffect } from "react";
import carServices from "../services/carServices";
import { Link, Navigate } from "react-router-dom";
import Loading from "./Loading";
import AuthServices from "../services/authServices";
import ReactPaginate from 'react-paginate';
import './Paginate.css';
import ContactoFooter from './ContactoFooter';

const IndexRent = () => {
    const [Car, setCar] = useState([]);
    const [currentPage, setCurrentPage] = useState(0);
    const itemsPerPage = 6;
    const [filtro, setFiltro] = useState("");
    const [error, setError] = useState(false);
    const isLoggedIn = AuthServices.getAuthToken();



    const offset = currentPage * itemsPerPage;
    const paginatedCars = Car.slice(offset, offset + itemsPerPage);

    useEffect(() => {
        getList();
    }, []);

    const handlePageChange = ({ selected }) => {
        setCurrentPage(selected);
    };

    const handleFiltroChange = (event) => {
        setFiltro(event.target.value);
        filtrodescription();
    };


    const getList = () => {
        const token = AuthServices.getAuthToken();
        if (token) {
            carServices.setAuthToken(token);
            console.log('Token :', token);
        } else {
            console.error("No se encontr칩 un token v치lido");
            console.log('Token :', token);
            return;
        }
        carServices.getAll()
            .then(response => {
                if (filtro) {
                    const filteredCar = response.data.filter((Car) =>
                        Car.licencePlate.toLowerCase().includes(filtro.toLowerCase())
                    );
                    setCar(filteredCar);
                } else {
                    setCar(response.data);
                }
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });

    };

    const filtrodescription = () => {
        const token = AuthServices.getAuthToken();
        if (token) {
            carServices.setAuthToken(token);
        } else {
            console.error("No se encontr칩 un token v치lido");
            return;
        }
        if (filtro != null) {
            carServices.getFiltro(filtro)
                .then((response) => {
                    setCar(response.data);
                    setError(false);
                    console.log(response.data);
                })
                .catch((e) => {
                    setError(true);
                    console.log(e);
                });
        } else {
            getList()
        }
    };


    return (
        <><div className="container">
            <div className="headerIndexRenr">
                <h2 style={{ fontFamily: 'Roboto', fontWeight: 'bold', textAlign: "center", color: "white" }}>The best vehicle option to rent with us</h2>
                <div className="ml-auto d-flex flex-column">
                            <div className="input-container">
                                <input
                                    type="text"
                                    className="form-control filtro flex-grow-1"
                                    value={filtro}
                                    onChange={handleFiltroChange}
                                    onBlur={handleFiltroChange}
                                    onKeyUp={handleFiltroChange}
                                    placeholder="Seach for licence plate"
                                />
                            </div>
                            {error && (
                                <small className="errorSmall" id="helpId" style={{marginTop:"1%"}}>
                                    <i className="bi bi-exclamation-circle"> Placa no encontrado</i>
                                </small>
                            )}
                        </div>
            </div>
            {Car.length === 0 ? (
                <Loading />
            ) : (
                <div className="d-flex flex-wrap">
                    {paginatedCars && paginatedCars.map((car) => (
                        <div key={car.id_car} className="card col-md-3 col-lg-3 mb-3" style={{ margin: "1%", marginLeft: "6%" }}>
                            <img src={car.image} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title" key={car.id_car}><i className="bi bi-car-front-fill"> </i>{car.description}</h5>
                                {car.typeCar.description === "4x4" ? (
                                    <p><i className="bi bi-pencil-square"> Descripci칩n: Veh칤culo {car.typeCar.description}, para uso off-road, uso familiar para llevar a toda la familia con la mayor comodidad</i></p>
                                ) : (
                                    <p><i className="bi bi-pencil-square"> Descripci칩n: Veh칤culo {car.typeCar.description}, para uso familiar para llevar a toda la familia con la mayor comodidad</i></p>
                                )}
                            </div>
                            <ul className="list-group list-group-flush bg-dark">
                                <li className="list-group-item"><i className="bi bi-gear-wide-connected"> </i>Cilindraje: {car.cylinder_capacity}</li>
                                <li className="list-group-item"><i className="bi bi-car-front"> </i>Capacidad: {car.capacity} Personas</li>
                                <li className="list-group-item"><i className="bi bi-calendar-date"> </i>Modelo: {car.model_year}</li>
                                <li className="list-group-item"><i className="bi bi-car-front-fill"> </i>Tipo: {car.typeCar.description}</li>
                                <li className="list-group-item"><i className="bi bi-card-text"> </i>Placa: {car.licencePlate}</li>
                            </ul>
                            <div className="card-body">
                                <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                                    {isLoggedIn ? (
                                        <Link className="btn btn-success" to={"/RentCreate"}>
                                            <i className="bi bi-plus-circle"> Alquilar</i>
                                        </Link>
                                    ) : (
                                        <Link className="btn btn-success" to={"/Login"}>
                                            <i className="bi bi-person-fill"></i> Alquilar
                                        </Link>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
            <div className="pagination-container">

                <ReactPaginate
                    previousLabel={<i className="bi bi-arrow-left-circle-fill left-paginate-arrow-indexrent"> </i>}
                    nextLabel={<i className="bi bi-arrow-right-circle-fill rigth-paginate"> </i>}
                    pageCount={Math.ceil(Car.length / itemsPerPage)}
                    onPageChange={handlePageChange}
                    containerClassName={'pagination'}
                    activeClassName={'active'}
                    previousClassName={'left-paginate-arrow-indexrent'}
                    nextClassName={'rigth-paginate'}
                    pageClassName={'indexrent-page-count'} />
            </div>

        </div>
            <ContactoFooter /></>
    );
};

export default IndexRent;
