import React, { useState, useEffect } from "react";
import carServices from "../services/carServices";
import typeCarServices from "../services/typecarServices";
import { Link } from 'react-router-dom';
import Swal from "sweetalert2";
import AuthServices from '../services/authServices';

const CarCreate = () => {


  const initialCarState = {
    licencePlate: "",
    description: "",
    imagen: null,
    cylinder_capacity: "",
    capacity: 0,
    model_year: "",
    typeCar: "",
  };

  const [Car, setCar] = useState(initialCarState);
  const [TypeCar, setTypeCar] = useState([]);
  const [Type, setType] = useState(null);
  const [errors, setErrors] = useState({});
  const [CarArray, setCarArray] = useState([]);
  const [Validat, setValidat] = useState(false);




  const handleInputChange = event => {
    const { name, value } = event.target;
    setCar({ ...Car, [name]: value });
  }

  const handleInputblur = event => {
    handleInputChange(event);
    setErrors(validationError(Car));
  }

  
  const handleImageChange = (files) => {
    if (files.length > 0) {
        const file = files[0];
        setCar({ ...Car, imagen: file });
    }
};

  useEffect(() => {
    getList();
    getCar();
    if (Validat)
      newCar()
  }, [Validat]);



  const getList = () => {
    const token = AuthServices.getAuthToken();
    if (token) {
      typeCarServices.setAuthToken(token);
      console.log('Token :', token);
    } else {
      console.error("No se encontró un token válido");
      console.log('Token :', token);
      return;
    }
    typeCarServices.getAll()
      .then(response => {
        setTypeCar(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });

  };

  const getCar = () => {
    const token = AuthServices.getAuthToken();
    if (token) {
      carServices.setAuthToken(token);
      console.log('Token :', token);
    } else {
      console.error("No se encontró un token válido");
      console.log('Token :', token);
      return;
    }
    carServices.getAll()
      .then(response => {
        setCarArray(response.data);
        console(response.data);
      }).catch(e => {
        console.log(e);
      })
  }

  const createCar = (e) => {
    e.preventDefault();
    const token = AuthServices.getAuthToken();
    if (token) {
      carServices.setAuthToken(token);
      console.log('Token :', token);
    } else {
      console.error("No se encontró un token válido");
      console.log('Token :', token);
      return;
    }
    var date = {
      idCar: Car.idCar, licencePlate: Car.licencePlate, description: Car.description,
      cylinder_capacity: Car.cylinder_capacity, capacity: Car.capacity,
      model_year: Car.model_year, image: Car.image, typeCar: ""
    }
    // licencePlate, description, cylinder_capacity, capacity, model_year,imagen, typeCar
    setErrors(validationError(Car));

    if (Object.keys(errors).length === 0) {
    console.log("li "+Car.licencePlate," des ", Car.description," cyly ", Car.cylinder_capacity," capaci ", Car.capacity, "model ",Car.model_year," imagen ", Car.imagen," id ty ", Type.id_typeCar);
      carServices.create(Car.licencePlate, Car.description, Car.cylinder_capacity, Car.capacity, Car.model_year, Car.imagen, Type.id_typeCar)
        .then(response => {
          setCar({
            licencePlate: response.data.licencePlate, description: response.data.description,
            image: response.data.image, cylinder_capacity: response.data.cylinder_capacity,
            capacity: response.data.capacity, model_year: response.data.model_year, typeCar: response.data.Type
          });
          setValidat(true);
          console.log(response.data);
          Swal.fire({
            position: 'top-center',
            icon: 'success',
            title: 'Vehículo Registrado Correctamente',
            showConfirmButton: false,
            timer: 2200
          })
        })
        .catch(e => {
          console.log(e);
        })
    }
  }

  const newCar = () => {
    setCar(initialCarState);
    setTypeCar([]);
    setValidat(false);
  }

  const validationError = (Car) => {
    let errors = {}

    CarArray.forEach(car => {
      if (car.licencePlate === Car.licencePlate) {
        errors.licencePlate = "Este numero de placa ya existe";
      }
    })

    return errors;
  }



  return (
    <div className="container ">
      <div className="submit-form">
        <div className="card  ">


          <div className="card-body ">
            <h4>Registrar Vehículo</h4>
