import React, { useState, useEffect } from "react";
import carServices from "../services/carServices";
import { Link, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import Loading from "./Loading"
import AuthServices from '../services/authServices';
import Paginate from './Paginate';

const CarList = (props) => {
    const [Car, setCar] = useState([]);
    let navigate = useNavigate;
    const [currentPage, setCurrentPage] = useState(0);
    const itemsPerPage = 10;
    const [filtro, setFiltro] = useState("");
    const [error, setError] = useState(false);

    useEffect(() => {
        getList();
    }, []);

    const handleSelectCar = (car) => {
        props.handleSelectCar(car);
    };
    

    const handleCarSelection = (car) => {
        handleSelectCar(car);
    };

    const handlePageChange = ({ selected }) => {
        setCurrentPage(selected);
    };

    const paginated = Car.slice(
        currentPage * itemsPerPage,
        (currentPage + 1) * itemsPerPage
    );

    const handleFiltroChange = (event) => {
        setFiltro(event.target.value);
        filtroLicencePlate();
    };

    const getList = () => {
        const token = AuthServices.getAuthToken();
        if (token) {
            carServices.setAuthToken(token);
            console.log('Token :', token);
        } else {
            console.error("No se encontró un token válido");
            console.log('Token :', token);
            return;
        }
        carServices.getAll()
            .then(response => {
                if (filtro) {
                    const filteredCar = response.data.filter((Car) =>
                        Car.licencePlate.toLowerCase().includes(filtro.toLowerCase())
                    );
                    setCar(filteredCar);
                } else {
                    setCar(response.data);
                }
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });

    };

    const filtroLicencePlate = () => {
        const token = AuthServices.getAuthToken();
        if (token) {
            carServices.setAuthToken(token);
        } else {
            console.error("No se encontró un token válido");
            return;
        }
        if (filtro != null) {
            carServices.getFiltro(filtro)
                .then((response) => {
                    setCar(response.data);
                    setError(false);
                    console.log(response.data);
                })
                .catch((e) => {
                    setError(true);
                    console.log(e);
                });
        } else {
            getList()
        }
    };

    const remove = (idCar) => {
        const token = AuthServices.getAuthToken();
        if (token) {
            carServices.setAuthToken(token);
            console.log('Token :', token);
        } else {
            console.error("No se encontró un token válido");
            console.log('Token :', token);
            return;
        }
        const swalWithBootstrapButtons = Swal.mixin({
            customClass: {
                confirmButton: 'btn btn-success',
                cancelButton: 'btn btn-danger'
            },
            buttonsStyling: false
        })

        swalWithBootstrapButtons.fire({
            title: 'Deseas eliminar este archivo?',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Si, eliminar!',
            cancelButtonText: 'No, cancelar!',
            reverseButtons: true
        }).then((result) => {
            if (result.isConfirmed) {
                carServices.remove(idCar)
                    .then(response => {
                        console.log(response.data);
                        swalWithBootstrapButtons.fire(
                            'Eliminado!',
                            'Tu archivo ha sido eliminado',
                            'Correctamente'
                        )
                        navigate(getList());
                    });
                swalWithBootstrapButtons.fire(
                    'Error!',
                    'Tu archivo esta ligado a otro, Primero elimine el archivo ligado a este Correctamente'
                )
            } else if (

                result.dismiss === Swal.DismissReason.cancel
            ) {
                swalWithBootstrapButtons.fire(
                    'Cancelado',
                    'No se ha eliminado nungun archivo'
                )
            }
        })
    };

    return (
        <div className="container ">
            {Car.length === 0 ? (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <Loading />
                    <Link className="btn btn-primary" to={"/CarCreate"}>
                        <i className="bi bi-plus-circle"> Registrar un Vehiculo </i>
                    </Link>
                </div>
            ) : (

                <div className="card text bg-light mb-3">

                    <div className="card-header d-flex justify-content-between">
                    {!props.hideButtons && (
                        <Link className="btn btn-secondary" style={{ height: "5vh" }} to={"/CarCreate"}>
                            <i className="bi bi-plus-circle"> Registrar un Vehiculo </i>
                        </Link>
                        )}
                        <div className="ml-auto d-flex flex-column">
                            <div className="input-container">
                                <input
                                    type="text"
                                    className="form-control filtro flex-grow-1"
                                    value={filtro}
                                    onChange={handleFiltroChange}
                                    onBlur={handleFiltroChange}
                                    onKeyUp={handleFiltroChange}
                                    placeholder="Seach for licence plate"
                                />
                            </div>
                            {error && (
                                <small className="errorSmall" id="helpId" style={{marginTop:"1%"}}>
                                    <i className="bi bi-exclamation-circle"> Placa no encontrado</i>
                                </small>
                            )}
                        </div>
                    </div>
                    <div className="card-body ">
                        <div className="table-responsive">
                            <table className="table table-striped border = 1">
                                <thead>
                                    <tr>
                                        <th scope="col">ID</th>
                                        <th scope="col">Placa</th>
                                        <th scope="col">Descripción</th>
                                        <th scope="col">Cilindraje</th>
                                        <th scope="col">Capacidad</th>
                                        <th scope="col">Modelo</th>
                                        <th scope="col">Tipo</th>
                                        {!props.hideButtons ? (
                                        <th scope="col">Link de Imagen</th>
                                        ):(<></>)}
                                    </tr>

                                </thead>
                                <tbody>
                                    {Car
                                        && paginated.map(
                                            (car) => (
                                                <tr key={car.idCar}>
                                                    <th scope="row">{car.idCar}</th>
                                                    <td>{car.licencePlate}</td>
                                                    <td>{car.description}</td>
                                                    <td>{car.cylinder_capacity}</td>
                                                    <td>{car.capacity}</td>
                                                    <td>{car.model_year}</td>
                                                    <td>{car.typeCar.description}</td>
                                                    {!props.hideButtons ? (
                                                    <td>{car.image}</td>
                                                    ):(<></>)}
                                                    <td>
                                                        <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                                                        {!props.hideButtons ? (
                                                            <>
                                                            <Link className="btn btn-secondary" to={"/CarUpdate/" + car.idCar}>
                                                                <i className="bi bi-gear"> Actualizar</i>
                                                            </Link>
                                                            <button className="btn btn-danger" onClick={() => remove(car.idCar)}>
                                                                <i className="bi bi-trash3"> Eliminar</i>

                                                            </button>
                                                            </>
                                                             ) : (
                                                                <button
                                                                className="btn btn-success"
                                                                onClick={() => {
                                                                    handleCarSelection(car)
                                                                }}
                                                                style={{ height: "10vh" }}
                                                            >
                                                                <i className="bi bi-send-plus-fill"> Agregar</i>
                                                                
                                                            </button>

                                                        )}

                                                        </div>

                                                    </td>
                                                </tr>

                                            )
                                        )}
                                </tbody>

                            </table>
                            <Paginate
                                pageCount={Math.ceil(Car.length / itemsPerPage)}
                                handlePageChange={handlePageChange}
                            />
                        </div>
                    </div>
                </div>
            )}
        </div>
 





    );
};

export default CarList;
