import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthServices from '../services/authServices';
import './Login.css';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await AuthServices.login({ username, password });
            const token = response.data.token;

            AuthServices.setAuthToken(token);
            navigate('/RentCreate'); // Redirige al componente deseado después del inicio de sesión exitoso
        } catch (error) {
            console.error(error);
            // Maneja el error de inicio de sesión, muestra un mensaje de error, etc.
        }
    };

    return (
        <div>
<h2 style={{color: "white" }} >Inicio de Seccion</h2>
        <div className="login-container">
            <form onSubmit={handleLogin}>
                <div class="mb-3">
                    <label for="username" class="form-label" style={{color: "white" }} >Username</label>
                    <input
                        type="text"
                        class="form-control"
                        id="username"
                        aria-describedby="emailHelp"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)
                        } />
                    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div class="mb-3">
                    <label for="password" class="form-label"style={{color: "white" }} >Password </label>
                    <input type="password"
                        class="form-control"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                <div class="mb-3 form-check">
                    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
                    <label class="form-check-label" for="exampleCheck1" style={{color: "white" }} >Check me out</label>
                </div>
                <button type="submit" class="btn btn-primary">Iniciar sesión</button>
            </form>


        </div>
        </div>
    );

}
export default Login;
